mapper.select:
SELECT i.examfeeitem_name as xmmc, o.patientcode, p.patientname as xm, p.id_sex as xb, u.user_name as sendPeople, w.name as wsjg, t.send_date as sendDate, o.id, t.back_date as backDate, us.user_name as backPeople FROM md_outside_charge_item o INNER JOIN md_outside_main t ON o.patientcode = t.patientcode INNER JOIN md_items i ON o.id_charge = i.id INNER JOIN md_peispatient p ON o.patientcode = p.patientcode LEFT JOIN sys_user u ON t.send_people = u.user_no LEFT JOIN sys_user us ON t.back_people = us.user_no LEFT JOIN md_wsjg w ON o.wsjg_id = w.id
1 = 1 AND o.is_delete = 0
AND t.send_date >= #{param.startTime}
AND t.send_date <= #{param.endTime}
AND o.wsjg_id = #{param.wsjg}
AND p.patientname like concat('%',#{param.patientname},'%')
AND p.patientcode like concat('%',#{param.patientcode})
ORDER BY t.createdate DESC

SELECT i.examfeeitem_name as xmmc, o.patientcode, p.patientname as xm, p.id_sex as xb, u.user_name as sendPeople, w.name as wsjg, t.send_date as sendDate, o.id, t.back_date as backDate, us.user_name as backPeople FROM md_outside_charge_item o INNER JOIN md_outside_main t ON o.patientcode = t.patientcode INNER JOIN md_items i ON o.id_charge = i.id INNER JOIN md_peispatient p ON o.patientcode = p.patientcode LEFT JOIN sys_user u ON t.send_people = u.user_no LEFT JOIN sys_user us ON t.back_people = us.user_no LEFT JOIN md_wsjg w ON o.wsjg_id = w.id
1 = 1 AND o.is_delete = 0
AND t.send_date >= #{param.startTime}
AND t.send_date <= #{param.endTime}
AND o.wsjg_id = #{param.wsjg}
ORDER BY t.createdate DESC
