mapper.select:
SELECT co.ddh, sc.int_id as tjm, co.ddmc, sc.khdwmc, b.fzx, STR_TO_DATE ( co.jhjqc, 'yyyy-mm-dd' ) AS jhjqc, STR_TO_DATE ( co.jhjqd, 'yyyy-mm-dd' ) AS jhjqd, co.xsjl, STR_TO_DATE ( co.createdate, 'yyyy-mm-dd' ) AS cjddrq FROM md_createorder co LEFT JOIN crm_sellcustomer sc ON sc.id = co.khdwmcid LEFT JOIN sys_branch b ON b.branch_id = co.fzxid
AND sc.id = #{param.customerId}
ORDER BY co.createdate DESC

SELECT p.patientname, STR_TO_DATE ( p.dateregister, '%Y-%m-%d' ) AS dateregister, co.ddmc as dd, p.examsuite_name as tc, p.org_depart, b.fzx, p.f_registered, p.f_readytofinal, p.jktjzt as jktjztNum, p.zytjzt as zytjztNum FROM md_peispatient p LEFT JOIN md_createorder co ON co.ddh = p.numorgresv LEFT OUTER JOIN sys_branch b ON b.branch_id = p.hospitalcode
AND P.ID_ORG = #{param.customerId}
AND p.dateregister >= #{param.startDate}
AND p.dateregister <= #{param.endDate}
AND p.patientname like concat('%',#{param.customerName},'%')
AND p.org_depart like concat('%',#{param.deptName},'%')
AND p.numorgresv = #{param.orderNo}
AND p.f_registered = 1 AND (p.f_readytofinal is null or p.f_readytofinal=0)
AND ( p.f_examstarted = 1 AND p.f_readytofinal = 1 AND ( p.jktjzt IS NULL OR p.jktjzt = 0 ) )
AND ( p.f_examstarted = 1 AND p.f_readytofinal = 1 AND p.jktjzt = 1 )
AND ( p.f_examstarted = 1 AND p.f_readytofinal = 1 AND p.jktjzt >= 2 ) AND p.jktjzt < 11
AND (p.f_examstarted = 1 AND p.f_readytofinal = 1 AND p.jktjzt = 11)
ORDER BY p.patientcode DESC
