mapper.select:
SELECT p.id, p.patientcode, p.patientname, p.id_sex, '' sex, p.age, p.phone, p.idcardno, p.medicaldate, s.creater, p.dateguidancereturned, p.org_name, s.notify_result, STR_TO_DATE( s.notify_time, '%Y-%m-%d %T' ) AS notifyTime, p.is_noticed FROM md_peispatient p LEFT JOIN ( SELECT * FROM md_sms_record sin2 WHERE NOT EXISTS ( SELECT 1 FROM md_sms_record sin1 WHERE sin1.patientcode = sin2.patientcode AND sin1.notify_type = sin2.notify_type AND sin1.createdate > sin2.createdate )) s ON s.patientcode = p.patientcode AND s.notify_type = '0'
p.f_isforreserve = 1
AND p.patientbizno IS NULL
AND p.patientbizno IS NOT NULL
AND ( p.f_registered = 0 OR p.f_registered IS NULL ) AND ( p.f_paused IS NULL OR p.f_paused = 0 )
AND P.doctorapply = #{param.userNo}
AND dateguidancereturned >= #{param.startTime}
AND dateguidancereturned <= #{param.endTime}
AND s.notify_result = #{param.notifyResult}
AND p.f_usecodehiden = #{param.useCodeHiden}
ORDER BY ( CASE WHEN p.dateguidancereturned >= #{beginDay} AND p.dateguidancereturned <= #{endDay} THEN 1 WHEN p.createdate IS NULL AND p.dateguidancereturned IS NULL THEN 3 ELSE 2 END ) ASC,( CASE WHEN p.dateguidancereturned IS NULL THEN p.createdate ELSE p.dateguidancereturned END ) DESC
ORDER BY IFNULL(S.NOTIFY_RESULT,1) ASC,P.MEDICALDATE ASC
