mapper.select:
SELECT DISTINCT i.id, i.examfeeitem_name as name, i.unitprice as price FROM md_peispatient p INNER JOIN md_review r ON r.patientcode = p.patientcode AND r.is_delete = 0 INNER JOIN md_review_project rp ON rp.review_id = r.id INNER JOIN md_items i ON i.id = rp.items_id
NOT EXISTS ( SELECT 1 FROM md_peispatient p2 WHERE p2.f_registered = 1 AND ( p2.inpatientno = p.patientcode OR ( p2.inpatientno = p.inpatientno AND p2.createdate > p.createdate )))
AND p.numorgresv = #{param.ddh}
AND p.id_org = #{param.orgId}
ORDER BY i.examfeeitem_name

SELECT p.patientcode, p.patientname, p.id_sex as idSex, p.phone, p.idcardno, p.org_name as orgName, p.numorgresv, p.examsuite_name as examsuiteName, p.inpatientno, p.createdate FROM md_peispatient p
p.zytjzt >= 9 AND EXISTS ( SELECT 1 FROM md_review r INNER JOIN md_review_project rp ON rp.review_id = r.id WHERE r.patientcode = p.patientcode )
AND p.numorgresv = #{param.ddh}
AND p.id_org = #{param.orgId}
ORDER BY p.patientcode DESC
