mapper.select:
select u.user_id, u.user_no, u.cid, d.dept_no, u.dept_id, u.nick_name, u.user_name, u.email, u.avatar, u.phonenumber, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time, u.remark, u.user_code, u.kingdee_account_no, u.sdiscount, u.ldiscount, u.isleader, u.is_doc, u.entry_date, u.superior_id, u.isbj, u.secret_password, u.recive_code, u.input_code, d.dept_name, d.leader from sys_user u left join sys_dept d on u.dept_id = d.dept_id where u.del_flag = '0'
AND u.user_id = #{userId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.status = #{status}
AND u.phonenumber like concat('%', #{phonenumber}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.dept_id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
AND (NOT EXISTS ( SELECT 1 FROM sys_user_branch ub WHERE ub.user_id = u.user_no ) OR EXISTS ( SELECT 1 FROM sys_user_branch ub WHERE ub.user_id = u.user_no AND ub.branch_id IN
#{brandId}
))

select distinct u.user_id, u.user_no, u.cid, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

mapper.update:
update sys_user
set avatar = #{avatar}
where user_name = #{userName}

update sys_user
set password = #{password}
where user_name = #{userName}

mapper.delete:
update sys_user
set del_flag = '2'
where user_id = #{userId}

mapper.select:
SELECT u.user_no, u.user_name, b.fzx AS fzxmc, u.isleader, u.input_code, u.cid FROM sys_user u INNER JOIN sys_user_dep ud ON ud.user_id = u.user_no INNER JOIN sys_dept dt ON dt.dept_no = ud.dep_id LEFT JOIN sys_branch b ON b.branch_id = u.cid
u.del_flag = 0
AND u.cid = #{param.cid}
AND u.user_name LIKE concat('%',#{param.userName},'%') OR u.input_code LIKE concat('%',#{param.userName},'%')
AND u.isleader = #{param.isleader}
AND dt.dept_id = #{param.deptId}
GROUP BY u.user_no

SELECT u.user_no AS id, u.input_code AS inputCode, u.user_name AS username, u.isleader, b.fzx, u.nick_name FROM sys_user u INNER JOIN sys_user_dep ud ON ud.user_id = u.user_no INNER JOIN sys_dept d ON d.dept_no = ud.dep_id INNER JOIN sys_branch b ON b.branch_id = u.cid
1 = 1 AND u.isleader = 0 AND u.del_flag = 0 AND d.dept_name = #{param.name}
AND u.cid = #{param.cid}
AND ( u.user_name LIKE concat('%',#{param.key},'%') or u.input_code like concat('%',#{param.key},'%') )
GROUP BY u.user_no

select u.* from sys_user u
u.del_flag = '0' and u.status = '0' and kingdee_account_no IS NOT NULL
AND u.cid = #{cId}
