mapper.select:
SELECT s.patientcode, s.total_time, s.summarize, s.offer, s.posistive, p.medicaltype, p.org_name FROM md_section_total s INNER JOIN md_peispatient p ON p.patientcode = s.patientcode
s.disease_health=#{dh} AND s.id!=#{smId} AND p.patientarchiveno=#{patientarchiveno}
ORDER BY s.total_time DESC

SELECT s.patientcode, STR_TO_DATE( s.total_time, '%Y-%m-%d' ) as totalTime, s.summarize, s.offer, s.posistive, p.medicaltype, p.org_name, (case when r.create_num is not null then 1 else 0 end) AS fpdf, r.url_pdf, 0 as isOld FROM md_section_total s INNER JOIN md_peispatient p ON p.patientcode = s.patientcode LEFT JOIN md_report r ON r.patientcode = s.patientcode AND r.disease_health = #{dh}
s.disease_health = #{dh} AND s.id != #{smId} AND p.patientarchiveno = #{patientarchiveno}
ORDER BY s.total_time DESC

SELECT s.patientcode, STR_TO_DATE( s.total_time, '%Y-%m-%d' ) as totalTime, s.summarize, s.offer, s.posistive, p.medicaltype, p.org_name, (case when r.create_num is not null then 1 else 0 end) AS fpdf, r.url_pdf, 0 as isOld FROM md_section_total s INNER JOIN md_peispatient p ON p.patientcode = s.patientcode LEFT JOIN md_report r ON r.patientcode = s.patientcode AND r.disease_health = #{dh}
s.disease_health = #{dh} AND s.id != #{smId} AND p.patientarchiveno = #{patientarchiveno}
ORDER BY s.total_time DESC

SELECT s.patientcode, to_char ( s.total_time, 'yyyy-mm-dd') as totalTime, s.summarize, s.offer, s.posistive, p.medicaltype, p.org_name, (case when r.create_num is not null then 1 else 0 end) AS fpdf, r.url_pdf, 1 as isOld FROM section_total s INNER JOIN peispatient p ON p.patientcode = s.patientcode LEFT JOIN report r ON r.patientcode = s.patientcode AND r.disease_health = #{dh}
s.disease_health = #{dh} AND s.id != #{smId} AND p.idcardno = #{idcardno}
ORDER BY s.total_time DESC

SELECT s.patientcode, to_char ( s.total_time, 'yyyy-mm-dd') as totalTime, s.summarize, s.offer, s.posistive, p.medicaltype, p.org_name, (case when r.create_num is not null then 1 else 0 end) AS fpdf, r.url_pdf FROM section_total s INNER JOIN PEISPATIENT_HIS p ON p.patientcode = s.patientcode LEFT JOIN report r ON r.patientcode = s.patientcode AND r.disease_health = #{dh}
s.disease_health = #{dh} AND s.id != #{smId} AND p.idcardno = #{idcardno}
ORDER BY s.total_time DESC
