DROP TABLE IF EXISTS `sys_department`;
CREATE TABLE `sys_department`  (
  `id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '部门id',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '部门名称',
  `description` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '描述',
  `parent` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '父级部门ID',
  `createdate` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `modifydate` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `is_delete` tinyint(1) NULL DEFAULT NULL COMMENT '是否已删除 0 未删除 1 已删除',
  `is_function` tinyint(1) NULL DEFAULT NULL COMMENT '是否为功能部门0为非功能科室1为功能科室',
  `input_code` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '拼音输入码',
  `xh` int NULL DEFAULT NULL COMMENT '序号',
  `sjbggs` tinyint NULL DEFAULT NULL COMMENT '数据报告格式',
  `jcdd` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '检查地点',
  `jklx` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '接口类型',
  `ksh` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '科室号',
  `imgpath` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '图片地址',
  `report_path_health` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '健康报告默认模板存放路径',
  `report_path_disease` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '职业报告默认模板存放路径',
  `add_pic_before` tinyint(1) NULL DEFAULT NULL COMMENT '是否最后追加图片',
  `report_sort` int NULL DEFAULT NULL COMMENT '科室报告排序',
  `dyd_xh` int NULL DEFAULT NULL COMMENT '导引单排序',
  `dyd_memo` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT 'dyd_memo',
  `kingdee_account_no` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '金蝶账号',
  `grey_imgpath` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '灰图路径',
  `is_showapp` tinyint(1) NULL DEFAULT NULL COMMENT '是否在APP显示：0否 1是',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '分中心部门表（对应原系统中的qx_cen_dept）' ROW_FORMAT = DYNAMIC;

