DROP TABLE IF EXISTS `sys_function_branch`;
CREATE TABLE `sys_function_branch`  (
  `id` varbinary(32) NOT NULL COMMENT '关联ID',
  `function_id` int UNSIGNED NOT NULL COMMENT '业务功能ID（关联数据表sys_function的ID）',
