  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_ssb_branch_id`(`branch_id` ASC) USING BTREE,
  INDEX `idx_ssb_function_id`(`function_id` ASC) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '业务功能-分中心关联' ROW_FORMAT = DYNAMIC;

