  `cid_name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '分中心字段名',
  `pid` int NULL DEFAULT NULL COMMENT '关联表sys_table_config的ID',
  `pkey_name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '关联表的关联字段名称（查分中心ID）',
  `cur_key_name` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '当前表的关联字段的名称（查分中心ID）',
  `sync_type` tinyint(1) NOT NULL DEFAULT 3 COMMENT '同步类型：1.线上至线下 2.线下至线上 3.双向',
  `need_sync` tinyint(1) NOT NULL DEFAULT 0 COMMENT '是否需要同步：0.不需要 1.同步至所有中心 2.指定中心',
  `sync_condition` tinyint(1) NULL DEFAULT 0 COMMENT '是否存在同步需要满足的条件',
