  `createdate` datetime NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modifydate` datetime NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_stc_table_name`(`table_name` ASC) USING BTREE,
  INDEX `idx_stc_need_sync`(`need_sync` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 532 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '数据表配置' ROW_FORMAT = DYNAMIC;

