  INDEX `idx_svi_proponent_id`(`proponent_id` ASC) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 2123 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '系统更新记录' ROW_FORMAT = DYNAMIC;

